import React, { useState, useEffect } from "react";
import { auth, db, signInWithGoogle, logout } from "./firebaseConfig";
import { doc, getDoc } from "firebase/firestore";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);

  // Fetch user details and role from Firestore when they log in
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
      setUser(currentUser);

      if (currentUser) {
        try {
          const userRef = doc(db, "users", currentUser.uid);
          const userSnap = await getDoc(userRef);

          if (userSnap.exists()) {
            setRole(userSnap.data().role); // âœ… Fetch role from Firestore
          } else {
            console.log("User role not found in Firestore.");
            setRole(null);
          }
        } catch (error) {
          console.error("Error fetching user role:", error);
        }
      } else {
        setRole(null);
      }
    });

    return () => unsubscribe();
  }, []);

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Insura</h1>

      {user ? (
        <div>
          <h2>Welcome, {user.displayName} ðŸ‘‹</h2>
          <img src={user.photoURL} alt="Profile" style={{ width: "100px", borderRadius: "50%" }} />
          <p>Email: {user.email}</p>
          <p><strong>Role:</strong> {role === "carrier" ? "Insurance Carrier" : "FSRA Staff"}</p>
          <button onClick={logout} style={{ padding: "10px", backgroundColor: "#ff4d4d", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}>
            Logout
          </button>
        </div>
      ) : (
        <div>
          <h2>Login</h2>
          <button onClick={() => signInWithGoogle("carrier")} style={{ padding: "10px", backgroundColor: "#4285F4", color: "white", border: "none", borderRadius: "5px", cursor: "pointer", margin: "5px" }}>
            Login as Insurance Carrier
          </button>
          <button onClick={() => signInWithGoogle("fsra")} style={{ padding: "10px", backgroundColor: "#34A853", color: "white", border: "none", borderRadius: "5px", cursor: "pointer", margin: "5px" }}>
            Login as FSRA Staff
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
