import { initializeApp } from "firebase/app";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut } from "firebase/auth";
import { getFirestore, doc, setDoc, getDoc } from "firebase/firestore";

const firebaseConfig = {
    apiKey: "AIzaSyC9XQrU79CXuj_s-5SWpP0s8knM2qyc2x8",
    authDomain: "sample-d7290.firebaseapp.com",
    projectId: "sample-d7290",
    storageBucket: "sample-d7290.firebasestorage.app",
    messagingSenderId: "1:755788443725:web:cdfc6af42193a4da9d6c04",
    appId: "YOUR_APP_ID",
    measurementId: "G-MCEZX0450J"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);

// Register New User and Assign Role (Manual Sign Up)
const registerWithEmail = async (email, password, role) => {
    try {
        const userCredential = await createUserWithEmailAndPassword(auth, email, password);
        const user = userCredential.user;

        // Save user details in Firestore with assigned role
        await setDoc(doc(db, "users", user.uid), {
            email: user.email,
            role: role,  // Either 'fsra' or 'carrier'
            uid: user.uid
        });

        console.log("User registered successfully");
    } catch (error) {
        console.error("Error registering user:", error.message);
    }
};

// Login Existing User
const loginWithEmail = async (email, password) => {
    try {
        const userCredential = await signInWithEmailAndPassword(auth, email, password);
        return userCredential.user;
    } catch (error) {
        console.error("Error logging in:", error.message);
    }
};

// Logout User
const logout = async () => {
    try {
        await signOut(auth);
        console.log("User logged out");
    } catch (error) {
        console.error("Error logging out:", error.message);
    }
};

export { auth, db, registerWithEmail, loginWithEmail, logout };
