import React, { useState, useEffect } from "react";
import { auth, loginWithEmail, logout, db } from "./firebaseConfig";
import { doc, getDoc, collection, getDocs } from "firebase/firestore";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [name, setName] = useState("Loading...");
  const [role, setRole] = useState("No Role Assigned");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [policyRequests, setPolicyRequests] = useState([]);
  const [chatResponse, setChatResponse] = useState("");
  const [query, setQuery] = useState("");

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
      setUser(currentUser);

      if (currentUser) {
        try {
          const userRef = doc(db, "users", currentUser.uid);
          const userSnap = await getDoc(userRef);

          if (userSnap.exists()) {
            const userData = userSnap.data();
            setName(userData.Name || "User");
            setRole(userData.role || "No Role Assigned");

            // Fetch pending policy requests for FSRA staff
            if (userData.role === "FSRA") {
              const requestsSnapshot = await getDocs(collection(db, "policyRequests"));
              setPolicyRequests(requestsSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
            }
          }
        } catch (error) {
          console.error("Firestore Fetch Error:", error.message);
        }
      }
    });

    return () => unsubscribe();
  }, []);

  // Function to send request to AI chatbot
  const handleChatbotQuery = async () => {
    if (!query) return;
    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer 
          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`
        },
        body: JSON.stringify({
          model: "gpt-4",
          messages: [
            { role: "system", content: "You are an FSRA policy expert. Respond based on FSRA policies and underwriting guidelines." },
            { role: "user", content: query }
          ]
        })
      });

      const data = await response.json();
      setChatResponse(data.choices[0].message.content);
    } catch (error) {
      console.error("AI Chatbot Error:", error.message);
    }
  };

  return (
    <div style={containerStyle}>
      <h1>Insura</h1>

      {user ? (
        <div style={contentStyle}>
          <h2>Welcome, {name} üëã</h2>
          <p><strong>Role:</strong> {role}</p>
          <button onClick={logout} style={buttonStyle}>Logout</button>

          {role === "FSRA" && (
            <>
              <h3>Pending Policy Requests</h3>
              {policyRequests.length === 0 ? (
                <p>No pending requests.</p>
              ) : (
                policyRequests.map(req => (
                  <div key={req.id} style={requestStyle}>
                    <p><strong>Request:</strong> {req.requestDetails}</p>
                    <p><strong>Submitted by:</strong> {req.name} ({req.email})</p>
                    <button style={approveButton}>Approve ‚úÖ</button>
                    <button style={rejectButton}>Reject ‚ùå</button>
                  </div>
                ))
              )}

              <h3>AI Chatbot (FSRA Policy Assistant)</h3>
              <textarea
                placeholder="Ask a policy-related question..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                style={textareaStyle}
              />
              <button onClick={handleChatbotQuery} style={buttonStyle}>Ask AI</button>
              {chatResponse && <p><strong>AI Response:</strong> {chatResponse}</p>}
            </>
          )}
        </div>
      ) : (
        <div style={contentStyle}>
          <h2>Login</h2>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} />
          <button onClick={() => loginWithEmail(email, password)} style={buttonStyle}>Login</button>
        </div>
      )}
    </div>
  );
}

/* Styling */
const containerStyle = { display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", padding: "20px", minHeight: "100vh", fontFamily: "Arial, sans-serif" };
const contentStyle = { maxWidth: "600px", width: "90%", textAlign: "center", background: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" };
const textareaStyle = { width: "90%", height: "100px", margin: "10px", padding: "10px", fontSize: "14px" };
const buttonStyle = { padding: "10px", fontSize: "16px", margin: "10px", backgroundColor: "#4285F4", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" };
const approveButton = { ...buttonStyle, backgroundColor: "#34A853" };
const rejectButton = { ...buttonStyle, backgroundColor: "#EA4335" };

export default App;
