import React, { useState } from "react";

function App() {
  const [query, setQuery] = useState("");
  const [chatResponse, setChatResponse] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // ✅ Update this with your backend URL
  const API_URL = "https://fsra-chatbot-backend.onrender.com";

  const handleChatbotQuery = async () => {
    try {
      const response = await fetch(`${API_URL}/chat`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ message: query })
      });

      const data = await response.json();
      setChatResponse(data.reply); // AI's response
    } catch (error) {
      console.error("AI Chatbot Error:", error.message);
    }
  };

  return (
    <div style={containerStyle}>
      <h1>Insura</h1>

      <div style={contentStyle}>
        <p><strong>Welcome, </strong></p>
        <p><strong>Role:</strong> {"role"}</p>
        <button onClick={() => console.log("Logout")} style={buttonStyle}>Logout</button>

        <h3>Pending Policy Requests</h3>
        <p>No pending requests.</p>

        <h3>AI Chatbot (FSRA Policy Assistant)</h3>
        <textarea
          placeholder="Ask a policy-related question..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={textareaStyle}
        />
        <button onClick={handleChatbotQuery} style={buttonStyle}>Ask AI</button>
        {chatResponse && <p><strong>AI Response:</strong> {chatResponse}</p>}
      </div>

      <div style={contentStyle}>
        <h2>Login</h2>
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} />
        <button onClick={() => console.log("Login")} style={buttonStyle}>Login</button>
      </div>
    </div>
  );
}

// ✅ Styling
const containerStyle = { display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" };
const contentStyle = { textAlign: "center", marginBottom: "20px" };
const buttonStyle = { padding: "10px", margin: "10px", cursor: "pointer" };
const textareaStyle = { width: "300px", height: "100px", marginBottom: "10px" };
const inputStyle = { padding: "8px", margin: "5px", width: "250px" };

export default App;
