import React, { useState, useEffect } from "react";
import { auth, signInWithGoogle, logout, db } from "./firebaseConfig";
import { doc, getDoc } from "firebase/firestore";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
      setUser(currentUser);
      if (currentUser) {
        // Get user role from Firestore
        const userRef = doc(db, "users", currentUser.uid);
        const userSnap = await getDoc(userRef);
        if (userSnap.exists()) {
          setRole(userSnap.data().role);
        }
      } else {
        setRole(null);
      }
    });
    return () => unsubscribe();
  }, []);

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Insura</h1>

      {user ? (
        <>
          <h2>Welcome, {user.displayName} ðŸ‘‹</h2>
          <img
            src={user.photoURL}
            alt="Profile"
            style={{ borderRadius: "50%", width: "100px", marginTop: "10px" }}
          />
          <p>Email: {user.email}</p>
          <p><strong>Role:</strong> {role ? role.toUpperCase() : "Loading..."}</p>
          <button onClick={logout} style={buttonStyle}>Logout</button>
        </>
      ) : (
        <>
          <h2>Choose Your Role</h2>
          <button onClick={() => signInWithGoogle("carrier")} style={buttonStyle}>
            Login as Insurance Carrier
          </button>
          <button onClick={() => signInWithGoogle("fsra")} style={{ ...buttonStyle, backgroundColor: "#34A853" }}>
            Login as FSRA Staff
          </button>
        </>
      )}
    </div>
  );
}

const buttonStyle = {
  padding: "10px 20px",
  fontSize: "16px",
  marginTop: "20px",
  cursor: "pointer",
  backgroundColor: "#4285F4",
  color: "white",
  border: "none",
  borderRadius: "5px",
  margin: "10px"
};

export default App;
