import React, { useState, useEffect } from "react";
import { auth, signInWithGoogle, logout, db, submitPolicyRequest } from "./firebaseConfig";
import { doc, getDoc, collection, getDocs, updateDoc } from "firebase/firestore";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [request, setRequest] = useState("");
  const [policyRequests, setPolicyRequests] = useState([]);

  // Fetch user details when they log in
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
      setUser(currentUser);

      if (currentUser) {
        try {
          const userRef = doc(db, "users", currentUser.uid);
          const userSnap = await getDoc(userRef);

          if (userSnap.exists()) {
            const userRole = userSnap.data().role;
            setRole(userRole);
          } else {
            console.log("No role found for user, defaulting to Carrier.");
            setRole("carrier"); // Default to carrier if no role found
          }
        } catch (error) {
          console.error("Error fetching user role:", error);
        }
      } else {
        setRole(null);
      }
    });

    return () => unsubscribe();
  }, []);

  // Fetch policy requests for FSRA Staff
  useEffect(() => {
    const fetchPolicyRequests = async () => {
      const querySnapshot = await getDocs(collection(db, "policyRequests"));
      setPolicyRequests(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };

    if (role === "fsra") {
      fetchPolicyRequests();
    }
  }, [role]);

  // Approve or reject policy requests
  const updateRequestStatus = async (id, status) => {
    const requestRef = doc(db, "policyRequests", id);
    await updateDoc(requestRef, { status });
    const updatedRequests = policyRequests.map(req =>
      req.id === id ? { ...req, status } : req
    );
    setPolicyRequests(updatedRequests);
  };

  return (
    <div style={containerStyle}>
      <h1>Insura</h1>

      {user ? (
        <div style={contentStyle}>
          <h2>Welcome, {user.displayName} ðŸ‘‹</h2>
          <img src={user.photoURL} alt="Profile" style={profilePicStyle} />
          <p>Email: {user.email}</p>
          <p><strong>Role:</strong> {role === "carrier" ? "Insurance Carrier" : "FSRA Staff"}</p>
          <button onClick={logout} style={buttonStyle}>Logout</button>

          {/* Carrier View: Submit Policy Change Request */}
          {role === "carrier" && (
            <div style={sectionStyle}>
              <h3>Submit a Policy Change Request</h3>
              <textarea
                placeholder="Describe the change..."
                value={request}
                onChange={(e) => setRequest(e.target.value)}
                style={textareaStyle}
              />
              <br />
              <button onClick={() => submitPolicyRequest(user, request)} style={buttonStyle}>
                Submit Request
              </button>
            </div>
          )}

          {/* FSRA Staff View: Review Requests */}
          {role === "fsra" && (
            <div style={sectionStyle}>
              <h3>Review Policy Change Requests</h3>
              {policyRequests.length === 0 ? <p>No requests available.</p> :
                policyRequests.map((req) => (
                  <div key={req.id} style={requestStyle}>
                    <p><strong>Request:</strong> {req.requestDetails}</p>
                    <p><strong>Submitted by:</strong> {req.name} ({req.email})</p>
                    <p><strong>Status:</strong> {req.status.toUpperCase()}</p>
                    {req.status === "pending" && (
                      <>
                        <button onClick={() => updateRequestStatus(req.id, "approved")} style={approveButton}>
                          Approve
                        </button>
                        <button onClick={() => updateRequestStatus(req.id, "rejected")} style={rejectButton}>
                          Reject
                        </button>
                      </>
                    )}
                  </div>
                ))
              }
            </div>
          )}
        </div>
      ) : (
        <div style={contentStyle}>
          <h2>Choose Your Role</h2>
          <button onClick={() => signInWithGoogle("carrier")} style={buttonStyle}>
            Login as Insurance Carrier
          </button>
          <button onClick={() => signInWithGoogle("fsra")} style={{ ...buttonStyle, backgroundColor: "#34A853" }}>
            Login as FSRA Staff
          </button>
        </div>
      )}
    </div>
  );
}

/* Responsive Styling */
const containerStyle = { display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", padding: "20px", minHeight: "100vh", fontFamily: "Arial, sans-serif" };
const contentStyle = { maxWidth: "600px", width: "90%", textAlign: "center", background: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" };
const profilePicStyle = { borderRadius: "50%", width: "100px", marginTop: "10px" };
const buttonStyle = { padding: "10px", fontSize: "16px", margin: "10px", backgroundColor: "#4285F4", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" };
const textareaStyle = { width: "90%", height: "100px", margin: "10px", padding: "10px", fontSize: "14px" };
const sectionStyle = { marginTop: "20px", width: "100%", textAlign: "left" };
const requestStyle = { border: "1px solid gray", padding: "10px", margin: "10px", borderRadius: "5px" };
const approveButton = { ...buttonStyle, backgroundColor: "#34A853" };
const rejectButton = { ...buttonStyle, backgroundColor: "#EA4335" };

export default App;
