import React, { useState, useEffect } from "react";

const API_URL = "https://fsra-chatbot-backend.onrender.com"; // Backend URL

function App() {
  // ✅ Define State Variables
  const [email, setEmail] = useState("");       // Stores email input
  const [password, setPassword] = useState(""); // Stores password input
  const [user, setUser] = useState(null);       // Stores user info after login
  const [pendingRequests, setPendingRequests] = useState([]); // Stores pending requests
  const [query, setQuery] = useState("");       // Stores chat input
  const [chatHistory, setChatHistory] = useState([]); // Stores chat history per user

  // ✅ Load user and chat history from localStorage
  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      const userData = JSON.parse(savedUser);
      setUser(userData);

      // Retrieve chat history for the logged-in user
      const savedChat = localStorage.getItem(`chatHistory_${userData.name}`);
      if (savedChat) {
        setChatHistory(JSON.parse(savedChat));
      }

      // ✅ Fetch pending requests if user is an FSRA role
      fetchPendingRequests(userData.role);
    }
  }, []);

  // ✅ Function to fetch pending requests (if FSRA role)
  const fetchPendingRequests = async (role) => {
    if (role.includes("FSRA")) {
      try {
        const response = await fetch(`${API_URL}/pending-requests`);
        const data = await response.json();
        setPendingRequests(data.requests || []);
      } catch (error) {
        console.error("Error fetching pending requests:", error.message);
      }
    }
  };

  // ✅ Function to handle user login
  const handleLogin = async () => {
    if (!email || !password) {
      alert("Please enter both email and password.");
      return;
    }

    try {
      const response = await fetch(`${API_URL}/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error("Invalid credentials");
      }

      const data = await response.json();
      setUser(data);
      localStorage.setItem("user", JSON.stringify(data));

      // ✅ Fetch pending requests for FSRA roles
      fetchPendingRequests(data.role);

      // ✅ Load chat history for the logged-in user
      const savedChat = localStorage.getItem(`chatHistory_${data.name}`);
      setChatHistory(savedChat ? JSON.parse(savedChat) : []);
    } catch (error) {
      alert(error.message);
    }
  };

  // ✅ Function to handle chatbot query
  const handleChatbotQuery = async () => {
    if (!query.trim()) return;

    try {
      const response = await fetch(`${API_URL}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: query }),
      });

      const data = await response.json();

      const newChat = [
        ...chatHistory,
        { user: "You", message: query },
        { user: "AI", message: data.reply },
      ];

      setChatHistory(newChat);

      // Store chat history specific to the logged-in user
      if (user?.name) {
        localStorage.setItem(`chatHistory_${user.name}`, JSON.stringify(newChat));
      }
    } catch (error) {
      console.error("AI Chatbot Error:", error.message);
    }
  };

  // ✅ Function to handle user logout
  const handleLogout = () => {
    if (user?.name) {
      localStorage.removeItem(`chatHistory_${user.name}`); // Clear chat for this user
    }
    localStorage.removeItem("user"); // Remove user session
    setUser(null);
    setChatHistory([]); // Clear chat UI
  };

  return (
    <div style={containerStyle}>
      {/* ✅ LOGIN SECTION */}
      {!user ? (
        <div style={loginContainerStyle}>
          <h2>Login</h2>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={inputStyle}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={inputStyle}
          />
          <button onClick={handleLogin} style={buttonStyle}>Login</button>
        </div>
      ) : (
        <>
          {/* ✅ USER INFO SECTION */}
          <div style={headerStyle}>
            <h1>Insura</h1>
            <p><strong>Welcome, {user.name}</strong></p>
            <p><strong>Role:</strong> {user.role}</p>
            <button onClick={handleLogout} style={buttonStyle}>Logout</button>
          </div>

          {/* ✅ PENDING REQUESTS SECTION */}
          {user.role.includes("FSRA") && pendingRequests.length > 0 && (
            <div style={pendingRequestsStyle}>
              <h3>Pending Requests</h3>
              <ul>
                {pendingRequests.map((request, index) => (
                  <li key={index}>{request}</li>
                ))}
              </ul>
            </div>
          )}

          {/* ✅ CHATBOT SECTION */}
          <div style={chatbotContainerStyle}>
            <h3>AI Chatbot (FSRA Policy Assistant)</h3>
            <textarea
              placeholder="Ask a policy-related question..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              style={textareaStyle}
            />
            <button onClick={handleChatbotQuery} style={buttonStyle}>Ask AI</button>
            <div style={chatHistoryStyle}>
              {chatHistory.map((chat, index) => (
                <p key={index}><strong>{chat.user}:</strong> {chat.message}</p>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

/** ✅ STYLING **/
const containerStyle = { display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", height: "100vh", backgroundColor: "#f8f9fa", fontFamily: "Arial, sans-serif" };
const loginContainerStyle = { display: "flex", flexDirection: "column", gap: "10px", backgroundColor: "#ffffff", padding: "20px", borderRadius: "8px", boxShadow: "0px 4px 6px rgba(0,0,0,0.1)" };
const headerStyle = { textAlign: "center", marginBottom: "20px" };
const chatbotContainerStyle = { width: "50%", backgroundColor: "#ffffff", padding: "20px", borderRadius: "8px", boxShadow: "0px 4px 6px rgba(0,0,0,0.1)", textAlign: "center" };
const pendingRequestsStyle = { backgroundColor: "#ffebcc", padding: "10px", borderRadius: "5px", marginBottom: "20px", textAlign: "center", boxShadow: "0px 2px 4px rgba(0,0,0,0.1)" };
const chatHistoryStyle = { textAlign: "left", maxHeight: "200px", overflowY: "auto", padding: "10px", border: "1px solid #ccc", borderRadius: "5px", marginTop: "10px" };
const inputStyle = { padding: "10px", width: "250px", marginBottom: "10px", borderRadius: "5px", border: "1px solid #ccc" };
const textareaStyle = { width: "100%", height: "80px", padding: "10px", borderRadius: "5px", border: "1px solid #ccc", marginBottom: "10px" };
const buttonStyle = { padding: "10px 15px", backgroundColor: "#007bff", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" };

export default App;
