import React, { useState, useEffect } from "react";
import { auth, signInWithGoogle, logout } from "./firebaseConfig";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>FSRA AI Web App</h1>

      {user ? (
        <>
          <h2>Welcome, {user.displayName} ðŸ‘‹</h2>
          <img
            src={user.photoURL}
            alt="Profile"
            style={{ borderRadius: "50%", width: "100px", marginTop: "10px" }}
          />
          <p>Email: {user.email}</p>
          <button onClick={logout} style={buttonStyle}>Logout</button>
        </>
      ) : (
        <button onClick={signInWithGoogle} style={buttonStyle}>Sign in with Google</button>
      )}
    </div>
  );
}

const buttonStyle = {
  padding: "10px 20px",
  fontSize: "16px",
  marginTop: "20px",
  cursor: "pointer",
  backgroundColor: "#4285F4",
  color: "white",
  border: "none",
  borderRadius: "5px"
};

export default App;
