import React, { useState, useEffect } from "react";
import { auth, loginWithEmail, logout, db } from "./firebaseConfig";
import { doc, getDoc } from "firebase/firestore";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [name, setName] = useState(""); // Fetch user name from Firestore
  const [role, setRole] = useState("No Role Assigned");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Check if user is logged in and fetch role & name from Firestore
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
      setUser(currentUser);

      if (currentUser) {
        const userRef = doc(db, "users", currentUser.uid);
        const userSnap = await getDoc(userRef);

        if (userSnap.exists()) {
          setName(userSnap.data().Name || "User"); // Fetch name from Firestore
          setRole(userSnap.data().role || "No Role Assigned"); // Fetch role from Firestore
        }
      }
    });

    return () => unsubscribe();
  }, []);

  return (
    <div style={containerStyle}>
      <h1>Insura</h1>

      {user ? (
        <div style={contentStyle}>
          <h2>Welcome, {name} ðŸ‘‹</h2>
          <p><strong>Role:</strong> {role}</p>
          <button onClick={logout} style={buttonStyle}>Logout</button>

          {/* Carrier View: Submit Policy Change Request */}
          {role === "carrier" && (
            <div style={sectionStyle}>
              <h3>Submit a Policy Change Request</h3>
              <textarea placeholder="Describe the change..." style={textareaStyle} />
              <br />
              <button style={buttonStyle}>Submit Request</button>
            </div>
          )}

          {/* FSRA Staff View: Review Requests */}
          {role === "fsra" && (
            <div style={sectionStyle}>
              <h3>Review Policy Change Requests</h3>
              <p>Requests will be displayed here...</p>
            </div>
          )}
        </div>
      ) : (
        <div style={contentStyle}>
          <h2>Login</h2>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={inputStyle}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={inputStyle}
          />

          {/* Login */}
          <button onClick={() => loginWithEmail(email, password)} style={buttonStyle}>Login</button>
        </div>
      )}
    </div>
  );
}

/* Styling */
const containerStyle = { display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", padding: "20px", minHeight: "100vh", fontFamily: "Arial, sans-serif" };
const contentStyle = { maxWidth: "600px", width: "90%", textAlign: "center", background: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" };
const inputStyle = { width: "90%", padding: "10px", margin: "5px", fontSize: "14px" };
const buttonStyle = { padding: "10px", fontSize: "16px", margin: "10px", backgroundColor: "#4285F4", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" };
const textareaStyle = { width: "90%", height: "100px", margin: "10px", padding: "10px", fontSize: "14px" };
const sectionStyle = { marginTop: "20px", width: "100%", textAlign: "left" };

export default App;
