import { initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider, signInWithPopup, signOut } from "firebase/auth";
import { getFirestore, doc, setDoc, getDoc } from "firebase/firestore";

const firebaseConfig = {
    apiKey: "AIzaSyC9XQrU79CXuj_s-5SWpP0s8knM2qyc2x8",
    authDomain: "sample-d7290.firebaseapp.com",
    projectId: "sample-d7290",
    storageBucket: "sample-d7290.appspot.com",
    messagingSenderId: "755788443725",
    appId: "1:755788443725:web:cdfc6af42193a4da9d6c04",
    measurementId: "G-MCEZX0450J"
};

const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);
export const db = getFirestore(app);
export const googleProvider = new GoogleAuthProvider();

// Function to sign in with Google and assign user role
export const signInWithGoogle = async (role) => {
    try {
        const result = await signInWithPopup(auth, googleProvider);
        const user = result.user;
        console.log("✅ User Signed In:", user);

        // Check if user already exists in Firestore
        const userRef = doc(db, "users", user.uid);
        const userSnap = await getDoc(userRef);

        if (!userSnap.exists()) {
            // If user does not exist, set role in Firestore
            await setDoc(userRef, {
                uid: user.uid,
                name: user.displayName,
                email: user.email,
                role: role, // Role is either "carrier" or "fsra"
            });
            console.log(`✅ New ${role} user added to Firestore`);
        } else {
            console.log("✅ User already exists in Firestore");
        }
    } catch (error) {
        console.error("❌ Error Signing In:", error);
    }
};

// Logout Function
export const logout = async () => {
    try {
        await signOut(auth);
        console.log("✅ User Signed Out");
    } catch (error) {
        console.error("❌ Error Signing Out:", error);
    }
};
